 import firebase from 'firebase/app'
 import database from 'firebase/database'
 
  async function Selects (path) {
   var JsonArray = []
   await ReadCycle(path , JsonArray)
   return JsonArray
  }


 async function ReadCycle (path , Arr) {
      await firebase.database().ref(path).once('value')
      .then((res) => {
         res.forEach((item) => {
         var info = item.val()
         console.log(info)
         Arr.push(info)
      })
        console.log("QueryFire : Query Finished      ...      Async Awesome")
         return Arr;
     })
 } 


 module.exports.Selects = Selects